import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

function EditIstilah() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    nama: "",
    kelas_kata: "",
    definisi: "",
    sinonim: "",
    terkait: "",
    emoji: ""
  });

  const handleUpdate = async () => {
    const updatedTerm = { word: istilah, definisi, sinonim };
  
    try {
      const response = await fetch(`http://localhost/glosariumzz-api/getDetail.php?id=${id}`, {
        method: 'PUT', // atau 'PATCH' jika sesuai dengan API yang ada
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedTerm),
      });
  
      if (response.ok) {
        // Jika update berhasil, arahkan ke halaman admin
        navigate("/admin");
      } else {
        console.error('Failed to update term');
      }
    } catch (error) {
      console.error('Error updating term:', error);
    }
  };
  

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost/glosariumzz-api/editIstilah.php", formData)
      .then((res) => {
        if (res.data.success) {
          alert("Berhasil disimpan");
          navigate("/admin");
        } else {
          alert("Gagal menyimpan");
        }
      })
      .catch((err) => {
        console.error("Gagal update:", err);
        alert("Terjadi kesalahan");
      });
  };

  return (
    <div className="edit-page">
      <h2>Edit Istilah</h2>
      <form onSubmit={handleSubmit}>
        <input name="nama" value={formData.nama} onChange={handleChange} placeholder="Nama" />
        <input name="kelas_kata" value={formData.kelas_kata} onChange={handleChange} placeholder="Kelas Kata" />
        <textarea name="definisi" value={formData.definisi} onChange={handleChange} placeholder="Definisi" />
        <input name="sinonim" value={formData.sinonim} onChange={handleChange} placeholder="Sinonim" />
        <input name="terkait" value={formData.terkait} onChange={handleChange} placeholder="Kata Terkait" />
        <input name="emoji" value={formData.emoji} onChange={handleChange} placeholder="Emoji" />
        <button type="submit">Simpan</button>
      </form>
    </div>
  );
}

export default EditIstilah;
